import * as React from 'react';
import { Button } from 'react-native-web';
import { Header,Container,Title,HeaderButtonLeft,HeaderButtonRight} from './styles';
import { View, FlatList} from 'react-native';
import { Entypo } from '@expo/vector-icons';
import { Ionicons } from '@expo/vector-icons';

function AddAlarmScreen({ navigation }) { 
    const Amigos = [
        {nome: "Amigo 1", key: "1"},
        {nome: "Amigo 2", key: "2"},
        {nome: "Amigo 3", key: "3"},
        {nome: "Amigo 4", key: "4"},
    ];
    return(
        <View>
            <Header>
                <HeaderButtonLeft>
                    <Ionicons name="chevron-back" size={20} color={"black"} />
                </HeaderButtonLeft>
                <HeaderButtonRight>
                    <Entypo name="dots-two-horizontal" size={20} color="black" />
                </HeaderButtonRight>
                <Title>Add Alarm</Title>
            </Header>
            <Container>
                <Button 
                title="Add" 
                onPress={() => navigation.navigate()}
                />
                <Button
                title="Go to"
                onPress={() => navigation.navigate('AddAlarm')}
                />
                <View>
                <FlatList
                    data={Amigos}
                    renderItem={(item, index) => {
                        return(
                            <View>
                                <Text>{item.nome}</Text>
                            </View>
                        )
                    }}
                />
                </View>
            </Container>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      marginTop: StatusBar.currentHeight || 0,
    },
    item: {
      backgroundColor: '#f9c2ff',
      padding: 20,
      marginVertical: 8,
      marginHorizontal: 16,
    },
    title: {
      fontSize: 32,
    },
  });
export default AddAlarmScreen;
