import * as React from 'react';
import { View, StyleSheet,ImageBackground, Image} from 'react-native';
import { ScrollView } from 'react-native-web';
import { Header,Container,HeaderButtonLeft,HeaderButtonRight,Button} from './styles';
import { Entypo } from '@expo/vector-icons';
import { Ionicons } from '@expo/vector-icons';


function BluebarryPancakeScreen({navigation,route}) {
  const item = route.params;
  return (
    <View>
      <Container>
        <Header>
          <HeaderButtonLeft>
            <Ionicons name="chevron-back" size={20} color={"black"} onPress={navigation.goBack} />
          </HeaderButtonLeft>
          <HeaderButtonRight>
            <Entypo name="dots-two-horizontal" size={20} color="black" />
          </HeaderButtonRight>
        </Header>
        <ImageBackground source={require ('../BluebarryPancake/Vector.png')} 
        style={{position: 'absolute', width: 289,height: 288,left: 43,top: 122}}/>
        <ScrollView showsVerticalScrollIndicator={false}>
          <View style={{justifyContent:'center', alignItems:'center', height:280}}></View>
          <View style={style.details}>
            <View style={{flexDirection:'row',justifyContent:"space-between",alignItems:'center'}}>
              <Text style={{position: 'absolute',width: 156,height: 24,left: 30,top: 386,
                fontFamily: 'Poppins',fontStyle: 'normal',fontWeight: 700,fontSize: 16,
                lineHeight: 24,color: '#1D1617'}}>Blueberry Pancake
              </Text>
              <Image source={require ('../BluebarryPancake/Heart.png')} style={{position: 'absolute',left: 8.33,
                right: 8.33,top: 10.42,bottom: 10.41}}/>
            </View>
            <Text style={style.detailsText}>Pancakes are some people's favorite breakfast, who doesn't like pancakes? 
              Especially with the real honey splash on top of the pancakes, of course everyone loves that! besides being Read More...
            </Text>
            <View style={{marginTop:40, marginBottom:40}}></View>
          </View>
        </ScrollView>
        <Button
          title="Add to Breakfast Meal"
          onPress={() => navigation.navigate('Meal Schedule')}
        />
      </Container> 
    </View>
  );
};
const style = StyleSheet.create({
  details: {
    paddingHorizontal: 20,
    paddingTop: 40,
    paddingBottom: 60,
    backgroundColor: '#FFFFFF',
    borderTopRightRadius: 40,
    borderTopLeftRadius: 40
  },
  detailsText: {
    position: 'absolute',
    width: 315,
    height: 72,
    left: 30,
    top: 609,
    fontFamily: 'Poppins',
    fontStyle: 'normal',
    fontWeight: 400,
    fontSize: 12,
    lineHeight: 18,
    Color: '#7B6F72',
  },
});
export default BluebarryPancakeScreen;
