import * as React from 'react';
import { TouchableOpacity, StyleSheet} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { Entypo } from '@expo/vector-icons';
import { Ionicons } from '@expo/vector-icons';

import MealPlannerScreen from './src/pages/MealPlanner';
import BreakfastScreen from './src/pages/Breakfast';
import BluebarryPancakeScreen from './src/pages/BluebarryPancake';
import MealScheduleScreen from './src/pages/MealSchedule';
import { ToastAndroid } from 'react-native-web';


const Stack = createNativeStackNavigator();

function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="MealPlanner">
        <Stack.Screen name="Meal Planner"
          component={MealPlannerScreen}
          options={{
          headerLeft: () => ( 
            <TouchableOpacity style={styles.esquerda}>
              <Ionicons name="chevron-back" size={20} color="black" />
           </TouchableOpacity>
          ),
          headerRight: () => (
            <TouchableOpacity style={styles.direita}>
              <Entypo name="dots-two-horizontal" size={20} color="#1D1617" />
            </TouchableOpacity>
          ),
          title: 'Meal Planner',
            headerTitleAlign: 'center' 
        }} />
        <Stack.Screen name="Breakfast" 
          component={BreakfastScreen}
          options={{
          headerShown: false,
          headerLeft: () => ( 
            <TouchableOpacity onPress={() => ToastAndroid.show('Left button pressed', ToastAndroid.SHORT)} >
              <Ionicons name="chevron-back" size={20} color="black" />
            </TouchableOpacity>
          ),
          headerRight: () => (
            <TouchableOpacity style={styles.direita}>
              <Entypo name="dots-two-horizontal" size={20} color="#1D1617" />
            </TouchableOpacity>
          ),
          title: 'Breakfast',
          headerTitleAlign: 'center'
        }} /> 
        <Stack.Screen name="Bluebarry Pancake"
          component={BluebarryPancakeScreen}
          options={{
          headerRight: () => (
            <TouchableOpacity style={styles.direita}>
              <Entypo name="dots-two-horizontal" size={20} color="#1D1617" />
            </TouchableOpacity>
          ),
          title: 'Bluebarry Pancake',
          headerTitleAlign: 'center'
        }} />
        <Stack.Screen name="Meal Schedule" 
          component={MealScheduleScreen}
          options={{
          headerRight: () => (
            <TouchableOpacity style={styles.direita}>
              <Entypo name="dots-two-horizontal" size={20} color="#1D1617" />
            </TouchableOpacity>
          ),
          title: 'Meal Schedule',
          headerTitleAlign: 'center',
        }} />   
      </Stack.Navigator>
    </NavigationContainer>
  );
}
const styles = StyleSheet.create({
  direita: {
    backgroundColor: 'red' ,
    borderRadius: 8 ,
    right: 30,
  },

});


export default App;
